struct PreparePlay : PlayerCommand {
  Context context =
    *gen::build(gen::set(&Context::entity_uri),
        gen::set(&Context::metadata),
        gen::set(&Context::pages,
            gen::map(genContextPage(gen::arbitrary()),
            [](ContextPage &&page) {
